                        m.put("name", fr.getName());
                        m.put("className", fr.getClassName());
                        m.put("urlPatternMappings", fr.getUrlPatternMappings());
                        m.put("servletNameMappings", fr.getServletNameMappings());
                        webFilterList.add(m);
